import React from 'react';
import { Grid } from '@material-ui/core';

import CardComponent from './Card/Card';
import styles from './Cards.module.css';

const Info = ({ data: { confirmed, recovered, deaths, lastUpdate, usStats } }) => {
  if (!confirmed) {
    return 'Loading...';
  }

  return (
    <div className={styles.container}>
      <Grid container spacing={3} justify="center">
        <CardComponent
          className={styles.infected}
          cardTitle="Infected"
          value={confirmed.value}
          lastUpdate={lastUpdate}
          cardSubtitle="Number of active cases from COVID-19."
        />
        <CardComponent
          className={styles.recovered}
          cardTitle="Recovered"
          value={recovered.value}
          lastUpdate={lastUpdate}
          cardSubtitle="Number of recoveries from COVID-19."
        />
        <CardComponent
          className={styles.deaths}
          cardTitle="Deaths"
          value={deaths.value}
          lastUpdate={lastUpdate}
          cardSubtitle="Number of deaths caused by COVID-19."
        />
        <CardComponent
          className={styles.current}
          cardTitle="Current Cases"
          value={confirmed.value-deaths.value-recovered.value}
          lastUpdate={lastUpdate}
          cardSubtitle="Number of current caused by COVID-19."
        />
        <CardComponent
          className={styles.recover_rate}
          cardTitle="Recover Rate"
          value={recovered.value/confirmed.value}
          lastUpdate={lastUpdate}
          cardSubtitle="Number of current caused by COVID-19."
        />
        <CardComponent
          className={styles.death_rate}
          cardTitle="Death Rate"
          value={death.value/confirmed.value}
          lastUpdate={lastUpdate}
          cardSubtitle="Number of current caused by COVID-19."
        />
      </Grid>
    </div>
  );
};

export default Info;
