import React from 'react';

import { Cards, CountryPicker, Chart, CovidTable } from './components';
import { fetchData, fetchAllData } from './api/';
import styles from './App.module.css';

import image from './images/image.png';

class App extends React.Component {
  state = {
    data: {},
    allData: [],
    country: '',
  }

  async componentDidMount() {
    const data = await fetchData();
    const allData = await fetchAllData();
    const tableData = [];
    allData.forEach(async country => {
      const countryData = await fetchData(country.iso3);
      tableData.push(countryData);
    });
    console.log("Table data", tableData);
    this.setState({ data });
  }

  handleCountryChange = async (country) => {
    const data = await fetchData(country);

    this.setState({ data, country: country });
  }

  render() {
    const { data, country, allData } = this.state;

    return (
      <div className={styles.container}>
        <img className={styles.image} src={image} alt="COVID-19" />
        <Cards data={data} />
        <CountryPicker handleCountryChange={this.handleCountryChange} />
        <Chart data={data} country={country} /> 
        <CovidTable countryData={allData} />
      </div>
    );
  }
}

export default App;
